  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]

  parts.forEach(value => {
    console.log(value) 
  })       

  const Header = (props) => {
    console.log(props)
    return (
      <div>
        <h1>{props.course}</h1>
      </div>
    )
  }

  const Content = (props) => {
    console.log(props)
    return (
      <div>
       <Part part={parts.name} exercises={parts.exercises}/>
       <Part part={parts.name} exercises={parts.exercises}/>
       <Part part={parts.name} exercises={parts.exercises}/>
      </div>
    )
  }

  const Total = (props) => {
    console.log(props)
    return (
      <div>
        <p>Number of exercises {props.exercises1 + props.exercises2 + props.exercises3}</p>
      </div>
    )
  }

  const Part = (props) => {
    console.log(props)
    return (
      <div>
        <p>{props.part} {props.exercises}</p>
      </div>
    )
  }

  return (
    <div>
      <Header course={course}/>
      <Content />
      <Total exercises1={parts.exercises} exercises2={parts.exercises} exercises3={parts.exercises}/>
    </div>
  )
}

export default App
