import React from 'react'

  const App = () => {
    const course = 'Half Stack application development'
    const part1 = {
      name: 'Fundamentals of React',
      exercises: 10
    }
    const part2 = {
      name: 'Using props to pass data',
      exercises: 7
    }
    const part3 = {
      name: 'State of a component',
      exercises: 14
    }

    const Header = (props) => {
      console.log(props)
      return (
        <div>
         <h1>{props.course}</h1>
        </div>
      )
    }

    const Total = (props) => {
      return (
        <div>
         <p>Number of exercises {part1.exercises + part2.exercises + part3.exercises}</p>
        </div>
      )
    }

    const Content = (props) => {
      return (
        <div>
          <part1>{props.name} {props.exercises}</part1>
          <part2>{props.name} {props.exercises}</part2>
          <part3>{props.name} {props.exercises}</part3>
        </div>
      )
    }

  return (
    <div>
      <Header course={course} />
      <Content />
      <Total exercises1={exercises1} exercises2={exercises2} exercises3={exercises3} />
    </div>
  )
}

export default App
