  }

  const Content = (props) => {
    console.log(props)
    return <Part parts={parts}/>

  }

  const Part = parts.map((name, exercises) => 
      <p key={parts.toString()}> {name, exercises}
      </p>);
      return ( 
        <p>{parts.map}</p>
      );


  const Total = (props) => {
    console.log(props)
    return <p>Number of exercices {props.exercises + props.exercises + props.exercises}</p>
  }

  return (
      <div>
        <Header course={course}/>
        <Content parts={parts}/>
        <Total parts={parts}/>
      </div>
  )
}

export default App
