import React from 'react'

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]

  const Header = (props) => {
    console.log(props)
    return <h1>{props.course}</h1>
  }

  const Content = () => {
    console.log()
    return (
    	<div>
      {parts.map(part => <p>{part.name} {part.exercises}</p>)}
      	</div>
    )
  }

  const Total = () => {
    console.log()
    return (
      <div>
        <Sum />
      </div>
    )
  }

  const sum = Array.from(parts.reduce(
    (m, {exercises}) => m.set(exercises, (m.get(exercises) || 0) + exercises), new Map
  ), ([exercises]) => ({exercises}));
  console.log(sum);

  return (
    <div>
      <Header course={course} />
      <Content parts={parts} />
      <Total parts={parts} />
    </div>
  )
}

export default App
