      neutral: clicks.neutral,
      bad: clicks.bad + 1
    }
    setBad(newClicks)
  }

  return (
    <div>
      <h1>give feedback</h1>
      <Button
        handleClick={handleGoodClick}
        text='good'
      />
      <Button
        handleClick={handleNeutralClick}
        text='neutral'
      />     
      <Button
        handleClick={handleBadClick}
        text='bad'
      />           
      <h1>statistics</h1>
    </div>
  )
}

export default App
